/* eslint-disable no-console */
import Koa from 'koa';
import { initSocketIO } from './socketio';


const app = new Koa();
const server = require('http').createServer(app.callback());

const PORT = process.env.PORT || 1337;

initSocketIO(server);

server.listen(PORT, () => {
  console.log(`Server listening on port: ${PORT}`);
});

export default app;

/* eslint-disable import/prefer-default-export */
/**
 * Definition d'une structure de base d'une action du jeu
 * @param
 */
export class Action {
  constructor(name, effect, affectYourself, affectOthers) {
    this.name = name || '';
    this.effect = effect || null;
    this.affectYourself = affectYourself || false;
    this.affectOthers = affectOthers || 0;
  }
}

/* eslint-disable import/prefer-default-export */
/**
 * Definition d'une structure de base d'une annonce du jeu
 * @param
 */
export class Announcement {
  constructor(name, date, description) {
    this.name = name || null;
    this.date = date || Date.now;
    this.description = description || null;
  }
}

