import { Trigger } from './trigger';

/* eslint-disable import/prefer-default-export */
const schedule = require('node-schedule');

/**
 * Test if the argument is a valid date
 * @param {Date} date A date object to test
 */
function isValidDate(date) {
  // eslint-disable-next-line no-restricted-globals
  return date && Object.prototype.toString.call(date) === '[object Date]' && !isNaN(date);
}
/**
 * Definition d'une structure de base d'un trigger du jeu : Une date
 */
export class Horloge extends Trigger {
  constructor(name, date, effect) {
    if (isValidDate(date)) {
      super(name, schedule.scheduleJob(date, effect), effect);
    } else {
      console.log('The provided date is not valid');
      super(null, null, null);
    }
  }
}

/* eslint-disable import/prefer-default-export */

/**
 * Definition d'une structure de base d'un trigger du jeu
 */
export class Trigger {
  constructor(name, event, effect) {
    this.name = name || null;
    this.event = event || null;
    this.effect = effect || null;
  }
}

import GameTemplate from './gameTemplate';

const currentGame = new GameTemplate('basicMurder');

// Create roles in the game
const meurtrier = currentGame.addRole('Meurtrier', 'Vous etes un très méchant meurtrier');
let detective;
for (let i = 0; i < 3; i += 1) {
  detective = currentGame.addRole('Detective', 'Vous êtes un gentil détective');
}

// Murderer mission
const kill = (_, others) => {
  others[0].setAlive(false);
  others[0].announce('You have been killed');
};
currentGame.addMission(
  'Tuer tout le monde',
  currentGame.addAction('Tuer', kill, false, 1),
  meurtrier,
  'Vous devez vous débarrasser le plus vite possible de tout les autres joueurs de la partie.',
);

// Detectives mission
const find = (_, others) => {
  others[0].getRole();
};
currentGame.addMission(
  'Trouver le meurtrier en testant les autres joueurs',
  currentGame.addAction('Inspecter', find, false, 1),
  detective,
);

export default currentGame;

